mapper.select:
select TABLE_SCHEMA as `user_namespace`, TABLE_NAME as `table_name` from information_schema.TABLES t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userDatabaseName})
and t.TABLE_NAME = #{tableName}

select TABLE_SCHEMA as `userDatabaseName`, table_name as `tableName`, COLUMN_NAME as `columnName`, DATA_TYPE as `dataType`, COLUMN_COMMENT as `comment`, COLUMN_DEFAULT as `columnDefault`, EXTRA as "extra" from information_schema.`COLUMNS` t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userDatabaseName})
and t.TABLE_NAME = #{tableName}
and t.COLUMN_NAME = #{columnName}

select count(*) from information_schema.TABLES t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userDatabaseName})
and t.TABLE_NAME = #{tableName}

select count(*) from information_schema.`COLUMNS` t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userDatabaseName})
and t.TABLE_NAME = #{tableName}
and t.COLUMN_NAME = #{columnName}
