mapper.select:
select TABLE_SCHEMA as `userSchemaName`, TABLE_NAME as `tableName`, TABLE_TYPE as `tableType` from information_schema.TABLES t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userSchemaName})
and t.TABLE_NAME = #{tableName}

select TABLE_SCHEMA as `userSchemaName`, TABLE_NAME as `tableName`, COLUMN_NAME as `columnName`, COLUMN_TYPE as `dataType`, COLUMN_KEY as `columnKey`, COLUMN_COMMENT as `comment`, COLUMN_DEFAULT as `columnDefault`, EXTRA as "extra" from information_schema.`COLUMNS` t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userSchemaName})
and t.TABLE_NAME = #{tableName}
and t.COLUMN_NAME = #{columnName}

select count(*) from information_schema.TABLES t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userSchemaName})
and t.TABLE_NAME = #{tableName}

select count(*) from information_schema.`COLUMNS` t where t.TABLE_SCHEMA like concat('randomInit_temp_', #{actionID}, '_%')
and t.TABLE_SCHEMA = concat('randomInit_temp_', #{actionID}, '_', #{userSchemaName})
and t.TABLE_NAME = #{tableName}
and t.COLUMN_NAME = #{columnName}
